// import logo from '../images/logo.svg'; how to import photos; code: src={logo}
import React, { Component } from 'react';
import './App.css';
import Home from './views/Home.js';
import Projects from './views/Projects.js';
import {BrowserRouter, Route} from 'react-router-dom';

// This basically just tells the App which javascript files to go to based on different URLs
class App extends Component {
  constructor(props) {
    super(props)
    this.state = {'selected': 'UXR'};
    this.changePage = this.changePage.bind(this);
  }

  // Function called when someone clicks on the text in a PageSelector
  changePage(selection, e) {
    console.log("changed state to", selection)
    this.setState({
      'selected': selection
    })
  }

  render() {
    return (
      <BrowserRouter>
        <div>
          <Route exact={true} path='/' render={() => (
            <Home projects={this.props.projects} changePage={this.changePage} selected={this.state.selected} />
          )}/>
          <Route
            exact={true}
            path='/projects/:id'
            render={(props) => (
              <Projects projects={this.props.projects} id={props.match.params.id} state={this.state.selected}/>
            )}
          />
        </div>
      </BrowserRouter>
    );
  }
}
export default App;
