import React from 'react';
// import logo from '../images/logo.svg'; how to import photos; code: src={logo}
import './App.css';

class PageSelector extends React.Component {
  constructor(props) {super(props)}  // boilerplate code

  // Passing down the changePage function so we can activate it when a click on a span happens
  render() {
    return (
      <div className="column">
        <span onClick={(e) => this.props.changePage(this.props.text, e)}>{this.props.text}</span>
      </div>
    );
  }
}

class Project extends React.Component {
  constructor(props) {
    super(props);
  }  // boilerplate code

  render() {
    return (
      <div className="projectcontainer">
        <div className="project">
          <p>{this.props.title}</p>
          <h2>{this.props.description}</h2>
        </div>
      </div>
    )
  }
}

class ProjectList extends React.Component {
  constructor(props) {super(props)}  // boilerplate code

  render() {
    return (
      <div className="projectlist">
        {this.props.projects.map(
          function(project, i) {
            return <Project title={project.title + " " + i} description={project.description} key={i}/>
          }
        )}
      </div>
        // {this.props.projects.map(function(u, i) {
        //   //console.log(u, i);
        //   <p>a</p>
        //   //<Project data={u}/>
        // })}
      //</div>
    );
  }
}


// Events: https://stackoverflow.com/questions/21285923/reactjs-two-components-communicating
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {'selected': 'UXR'};
    this.changePage = this.changePage.bind(this);
  }  // boilerplate code

  // Function called when someone clicks on the text in a PageSelector
  changePage(selection, e) {
    this.setState({
      'selected': selection
    })
  }

  render() {
    const selected = this.state.selected;
    console.log(selected)
    const defaultOrder = ["Graphic Design", "UXR", "Me"] // This is constant

    // Figure out what the order of the header should be given on the state
    let headerOrder;
    headerOrder=defaultOrder; //
    if(defaultOrder.find(selected) == 0) headerOrder = ["Me", "Graphic Design", "UXR"] // Shift Right
    else if(defaultOrder.find(selected) == 2) headerOrder = ["UXR", "Me", "Graphic Design"] // Shift Left

    return (
      <div className="App">
        <div className="mainheader">
          <h1>Jen Goldberg</h1>
          <div className="pageselections">
            { // the "this" at the end of this allows us to use changePage inside the map function
              headerOrder.map(function(u, i) {
                return <PageSelector text={u} key={i} changePage={this.changePage}/>
              }, this)
            }
          </div>
        </div>

        <div className="main">
          <div className="sidenav">" "</div>
          <div className="content">
            <ProjectList projects={this.props.projects}/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
